properties.directives:
set-column FLAG_EXIST if (!empty(REF_SERV_NUM_ORIG) && SERV_NUM_ORIG == REF_SERV_NUM_ORIG) {'1';} else {'0';}
set-column FLAG_access if(BUSINESS_LN =^ '900') {'1';} else {'0';}
set-column FLAG_component if(BUSINESS_LN !^ '900') {'1';} else {'0';}
set-column FLAG_componentAC if(BUSINESS_LN !^ '900' && SERVICE_STATUS == 'AC') {'1';} else {'0';}
set-column FLAG_componentDE if(BUSINESS_LN !^ '900' && SERVICE_STATUS == 'DE') {'1';} else {'0';}
set-column flag_concat FLAG_EXIST+FLAG_access+FLAG_component+FLAG_componentAC+FLAG_componentDE
set-column queue if (flag_concat == '01110' || flag_concat== '10110' || flag_concat == '11000') {'released';} else {'wait';}
#pragma load-directives now;
now :LoadDate
set-column curr_dt LoadDate.toString()
parse-as-date :curr_dt UTC
set-column loading_time curr_dt_1
set-column RAW_CREATION_DT if (FLAG_EXIST == '0'){loading_time} else {RAW_CREATION_DT}
set-column RAW_LAST_MODIFIED_DT if (FLAG_EXIST == '1'){loading_time} else {RAW_LAST_MODIFIED_DT}
