properties.script:
function transform(input, emitter, context) {
var stri=input.DEACTIVATION_DATE.split("T",1) ;
var myDate = new Date(stri[0]);
var myDate1 = new Date(stri[0]);
var myDate2 = new Date(stri[0]);
var D1=myDate1.setHours( myDate1.getHours() + 1 );
input.DEACTIVATION_DATE_1=D1;
var D2=myDate2.setHours( myDate2.getHours() + 2 );
input.DEACTIVATION_DATE_2=D2;
var D3=myDate.setHours( myDate.getHours());
input.DEACTIVATION_DATE=D3;
emitter.emit(input);
 }

properties.directives:
#pragma load-directives now;
now:LoadDate
set-column curr_dt LoadDate.toString()
parse-as-date :curr_dt UTC
set-column SubscriptionStatusHistory_loading_dt curr_dt_1

set-column HOT_LINE_DATE  (HOT_LINE_TEMP=='19000101')?null:HOT_LINE_DATE
set-column SERVICE_START_DATE  (SERVICE_START_DATE_TEMP=='19000101')?null:SERVICE_START_DATE
set-column REACTIVATION_DATE  (REACTIVATION_DATE_TEMP=='19000101')?null:REACTIVATION_DATE
set-column DEACTIVATION_DATE  (DEACTIVATION_DATE_TEMP=='19000101')?null:DEACTIVATION_DATE
set-column INITIAL_PAYMENT_DATE  (INITIAL_PAYMENT_DATE_TEMP=='19000101')?null:INITIAL_PAYMENT_DATE


set-column :stateTransition_toStatusCode  exp:{if (FLG_DEACT_REACT == 1)  {if (FLG_SERVICE_STATUS_ACTIVE_DEACTIVE == 1 && (REASONCODE= 'Barred' || REASONCODE== 'Disconnection') ) {stateTransition_toStatusCode="";} else {if (FLG_SERVICE_STATUS_ACTIVE_DEACTIVE == 0 && REASONCODE == 'Disconnection' ) {stateTransition_toStatusCode="";} 	   else{stateTransition_toStatusCode=SERVICE_STATUS;}}} else 	{stateTransition_toStatusCode=SERVICE_STATUS;} }
set-column :stateTransition_subReasonCode exp:{ if (REASONCODE== 'Connection') { stateTransition_subReasonCode=ACTIVATION_REASON }  else { if (REASONCODE== 'Disconnection') { stateTransition_subReasonCode=DEACTIVATION_REASON} else {if (REASONCODE== 'Reconnection') { stateTransition_subReasonCode=ACTIVATION_REASON }else {if (REASONCODE== 'FirstPayment') {stateTransition_subReasonCode=""}else {if (REASONCODE== 'Barred' && !(empty(HOT_LINE_DATE)) ) { stateTransition_subReasonCode=HOT_LINE_REASON }else {if (REASONCODE== 'Barred' && empty(HOT_LINE_DATE) ) { stateTransition_subReasonCode="" } else{stateTransition_subReasonCode=' '}}}}}} }
set-column :stateTransition_reasonCode  exp:{ if (SOURCE_SYS = 'PPB' && SERVICE_STATUS =~ ["AC","AL","FZ","TD"] &&  ((empty(SERVICE_START_DATE) || SERVICE_START_DATE > SubscriptionStatusHistory_loading_dt || empty(INITIAL_PAYMENT_DATE) || INITIAL_PAYMENT_DATE > SubscriptionStatusHistory_loading_dt ) && NETWORK_SERVICE_STATUS != 'DE')) {stateTransition_reasonCode='Promotion';} else {stateTransition_reasonCode=REASONCODE;} }
set-column :stateTransition_effectiveDateTime exp:{ if (FLG_DEACT_REACT_RAW == 1)   { if (SERVICE_STATUS == 'AC')  { if  ( REASONCODE= ='Disconnection')  { stateTransition_effectiveDateTime= DEACTIVATION_DATE;}          else { 	          if  ( REASONCODE= ='Barred') {stateTransition_effectiveDateTime=DEACTIVATION_DATE_1;} else {stateTransition_effectiveDateTime=DEACTIVATION_DATE_2;}			  }	} else 	{	if  ( REASONCODE= ='Disconnection')         { stateTransition_effectiveDateTime= DEACTIVATION_DATE_1;} 	   else {stateTransition_effectiveDateTime= DEACTIVATION_DATE;}		 } } else {  if(REASONCODE=='Connection') { stateTransition_effectiveDateTime = SERVICE_START_DATE;}else { if(REASONCODE=='Disconnection') { stateTransition_effectiveDateTime=DEACTIVATION_DATE;}else { if(REASONCODE=='Reconnection') { stateTransition_effectiveDateTime=REACTIVATION_DATE;}else { if(REASONCODE=='Barred') { stateTransition_effectiveDateTime=HOT_LINE_DATE;}else { if(REASONCODE=='FirstPayment') {stateTransition_effectiveDateTime=INITIAL_PAYMENT_DATE;}       else {stateTransition_effectiveDateTime=null;}}}}} }  }

set-column start_dttm stateTransition_lastModifiedDate

